import osimport csvcwd = os.getcwd()print(cwd)  #%%csvpath = os.path.join("..", "Resources", "web_starter.csv")# csvpath = "../Resources/web_starter.csv"#csvpath = "/Users/mikenlee/Documents/Personal/analytics/GWU/gitlab_repo/gwu-arl-data-pt-09-2020-u-c/01-Class-Activities/03-Python/2/Activities/11-HW_UdemyZip/Resources/web_starter.csv"title = []price = []sub_count = []n_reviews = []length = []# %%    with open(csvpath) as csvfile:    # CSV reader specifies delimiter and variable that holds contents    csvreader = csv.reader(csvfile, delimiter=',')    print(csvreader)    # Read the header row first (skip this step if there is now header)    # csv_header = next(csvreader)    # print(f"CSV Header: {csv_header}")    data = list(line for line in csvreader)    # print(data)    row_count = len(data)    # row_count = sum(1 for rows in csvreader)    print(row_count)        for row in data:    # Read each row of data after the header    # for row in csvreader:        title.append(row[1])        price.append(row[4])        sub_count.append(row[5])        n_reviews.append(row[6])        length.append(row[9])        print(title)              # %%udemy = zip(title, price, sub_count, n_reviews, length)#%%output_file = "/Users/mikenlee/Documents/Personal/analytics/GWU/GWU_dataAnalytics_HW/03_python/mini/udemy.csv"# open the output file, create a header row, and then write the zipped object to the csvwith open(output_file, "w", newline='') as datafile:    writer = csv.writer(datafile)    writer.writerow(["title", "price", "sub_count", "n_reviews", "length"])    writer.writerows(udemy)# %%# prints out to terminal but zipped file is emptied/exhausted after this for loop iteration # re-run above cells to re-populate zip file# for rows in udemy:#     print(rows)